class Solution:
    def levelOrder(self, root: Optional[TreeNode]) -> List[List[int]]:
        if not root:
          return []
        levels = []
        def bfs(node, level):
          if not node:
            return
          if len(levels) == level:
            levels.append([])
          levels[level].append(node.val)
          bfs(node.left, level + 1)
          bfs(node.right, level +1)
        bfs(root, 0)
        return levels```

